import React, { useEffect, useState } from 'react'
import Authors from './components/Authors'
import Books from './components/Books'
import NewBook from './components/NewBook'
import { gql, useLazyQuery, useQuery } from '@apollo/client'

const ALL_AUTHORS = gql`
  query {
    allAuthors {
      name
      born 
      bookCount 
    }
  }
`

const ALL_BOOKS = gql`
  query {
    allBooks {
      title 
      author 
      published 
    }
  }
`

const App = () => {
  const result = useQuery(ALL_AUTHORS)
  const [getBooks, books] = useQuery(ALL_BOOKS)
//  const [result, setResult] = useQuery(ALL_AUTHORS)
//  const [getBooks, resu] = useLazyQuery(ALL_BOOKS)
//  const [getBooks, books] = useLazyQuery(ALL_BOOKS)
  const [page, setPage] = useState('authors')

  const showPage = (content) => {
//    console.log('in showPage')
//    console.log('content:', content)
    if (content === 'books') {
      getBooks()
      console.log(books)
    }
//    console.log('resu.data3', resu.data)
    setPage(content)
//    console.log('page:', page)
  }

/*
  useEffect(() => {
    if (resu.data) {
      setBooks2(resu.data.allBooks)
      console.log('books2', books2)
    } 
    console.log('resu.data2', resu.data)
    console.log('books22', books2)
  }, [resu])

  console.log('resu.data1', resu.data)
  console.log('result.data', result.data)
  */

  if (result.loading)  {
    return <div>loading...</div>
  }

  return (
    <div>
      <div>
        <button onClick={() => showPage('authors')}>authors</button>
        <button onClick={() => showPage('books')}>books</button>
        <button onClick={() => showPage('add')}>add book</button>
      </div>

      <Authors
        show={page === 'authors'} authors={result.data.allAuthors}
      />

      <Books
//        show={page === 'books'} books={books.data.allBooks}
        show={page === 'books'} books={null}
      />

      <NewBook
        show={page === 'add'}
      />

    </div>
  )
}

export default App
