import React, { useEffect, useState } from 'react'
import Authors from './components/Authors'
import Books from './components/Books'
import NewBook from './components/NewBook'
import { gql, useLazyQuery, useQuery } from '@apollo/client'

const ALL_AUTHORS = gql`
  query {
    allAuthors {
      name
      born 
      bookCount 
    }
  }
`

const ALL_BOOKS = gql`
  query {
    allBooks {
      title 
      author 
      published 
    }
  }
`

const App = () => {
  const result = useQuery(ALL_AUTHORS)
  const bookResult = useQuery(ALL_BOOKS)
  const [page, setPage] = useState('authors')

  /*
  useLazyQuery sopisi kirjojen hakemiseen mut ei vaa onnistunu
  luomaan ratkaisua joka suorittaisi haun books-napin painalluksesta.
  sain toimimaan ratkaisun, jossa authors-sivulla on nappi 
  (kommentoitu alempana), joka kutsuu getBooks(). painetaan tätä 
  nappia ja siirrytään books-sivulle, ja kirjat näkyvät sivulla. 
  ehkä tässä on jotain piirtojärjestyshäikkää. useEffectin 
  käyttöäkin yritetty
  */

  if (result.loading || bookResult.loading)  {
    return <div>loading...</div>
  }

  return (
    <div>
      <div>
        <button onClick={() => setPage('authors')}>authors</button>
        <button onClick={() => setPage('books')}>books</button>
        <button onClick={() => setPage('add')}>add book</button>
      </div>

      <Authors
        show={page === 'authors'} authors={result.data.allAuthors}
      />

      <Books
        show={page === 'books'} books={bookResult.data.allBooks}
      />

      <NewBook
        show={page === 'add'}
      />

    </div>
  )

//        <button onClick={() => { getBooks(); showPage('books'); }}>books</button>
}

export default App
