import React, { useEffect, useState } from 'react'
import Authors from './components/Authors'
import Books from './components/Books'
import NewBook from './components/NewBook'
import { gql, useLazyQuery, useQuery } from '@apollo/client'

const ALL_AUTHORS = gql`
  query {
    allAuthors {
      name
      born 
      bookCount 
    }
  }
`

const ALL_BOOKS = gql`
  query {
    allBooks {
      title 
      author 
      published 
    }
  }
`

const App = () => {
  const result = useQuery(ALL_AUTHORS)
//  const [result, setResult] = useQuery(ALL_AUTHORS)
//  const [getBooks, resu] = useLazyQuery(ALL_BOOKS)
  const [books, resu] = useLazyQuery(ALL_BOOKS)
  const [books2, setBooks] = useState(null)
  const [page, setPage] = useState('authors')


  useEffect(() => {
    if (resu.data) {
      setBooks(resu.data.allBooks)
    }
  }, [resu])

  if (result.loading)  {
    return <div>loading...</div>
  }

  return (
    <div>
      <div>
        <button onClick={() => setPage('authors')}>authors</button>
        <button onClick={() => setPage('books')}>books</button>
        <button onClick={() => setPage('add')}>add book</button>
      </div>

      <Authors
        show={page === 'authors'} authors={result.data.allAuthors}
      />

      <Books
        show={page === 'books'} books={books2}
      />

      <NewBook
        show={page === 'add'}
      />

    </div>
  )
}

export default App
